import Link from 'next/link';
import MasterPage from '../components/Master';
import fetch from 'isomorphic-unfetch';
import Precio from '../components/Precio';

const index = (props) =>{
    return(
        <MasterPage>
            <div className="row">               
                <div className="col-12">
                    <h2>Bitcoin</h2>
                    <Precio 
                        precio={props.precioBitcoin}
                    />
                </div>

                <div className="col-md-8">
                    <h2>Noticias</h2>
                </div>

                <div className="col-md-4">
                    <h2>Destacados</h2>                    
                </div>


            </div>
        </MasterPage>
    )
}

// al cargarse el componenete se ejecuta esta funcion y guarda
// los resultados como props
index.getInitialProps = async () =>{
    // CryptoCompare https://min-api.cryptocompare.com/data/price?fsym=BTC&tsyms=USD
    const precio = await fetch(`https://api.coinmarketcap.com/v2/ticker/1/`);
    const rst = await precio.json();
    console.log(rst.data.quotes.USD)
    return {
        precioBitcoin : rst.data.quotes.USD
    }}

export default index;
